import React , { useEffect, useState } from 'react';

import Weather from './components/Weather';


import './App.css';

const App = () => {

  const APP_KEY = "656c98d724799f2308debe601f2c29b3";


  const [weather, setWeather] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect( () => {


  const getWeather = async () => {
    setLoading(true);
    const response = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${APP_KEY}`);
    const data = await response.json();
    setWeather(data.main);
    console.log(data);
    setLoading(false);
  }

  getWeather();

  },[]);



  return (
    <div className="App" >
      <h1 >Weather-Finder</h1>  
      <form className="search-form " >
      <input className="search-bar" type = "text" name =" country" placeholder = "Country..." />
      <button className ="btn-btn-primary" >
                Search
            </button>
            </form>


      <Weather weather={weather } loading={loading} />
    </div>

  );

};

export default App;
